// Complete the isValid function below.
function isValid(s) {
  let times = {};
  for (let i = 0; i < s.length; i++) {
    const element = s[i];
    if (times[element]) {
      times[element]++;
      continue;
    }
    times[element] = 1;
  }
  let count,
    diffFlag = false,
    index = 0;
  console.log(times);
  for (let element in times) {
    index++;
    const num = times[element];
    if (!count) {
      count = num;
      continue;
    }
    const diff = count - num;

    if (Math.abs(diff) > 0) {
      if (diffFlag) {
        return "NO";
      }
      diffFlag = !diffFlag;
    }
  }
  return "YES";
}

console.log(
  isValid(
    ""
  )
);
